import React from 'react';
import './mission.css';

class Form extends React.Component {
  constructor() {
    super();
    this.state = {
                  mission: "",
                  name: '',
                  planet: ""
                  }
  };
    render(){
      return(
        <>
          <h1> Mission to Mars Registration Form </h1>
          <div>
            <form onChange={this.handleChange}>
              <label htmlFor='Mision'> Mission </label>
                <br />
              <input type="text" placeholder="Mission" name="mission" value="" />
              <br />

              <label htmlFor='Name'> What is your name? </label>
                <br />
              <input type='text' placeholder='Your name' name="name" value="" />
              <br />

              <label htmlFor='Birth'> What is your date of birth? </label>
                <br />
              <input type="date" id="date" name="birth" value="" min="1980-01-01" max="2019-01-01" required/>
              <span class="validity"></span>
              <br />

              <label htmlFor='country'>What is your country of origin?</label>
              <select> </select>
              <br />

              <label htmlFor='dietary'>What is your dietary preference?</label>
              <select> </select>
              <br />

              <label htmlFor='dietary'>Why do you want to be a Mars explorer? </label>
              <input type="text" placeholder="Why?" name="explorer" value="" />
              <br />

              <button type="submit"> </button>
            </form>

          </div>
        </>
      )
    }
}

export default Form;
