import React from 'react';
import './mission.css';

class Form extends React.Component {
  constructor() {
    super();
    this.state = {
                  mission: "",
                  name: '',
                  planet: ""
                  }
  };
    render(){
      return(
        <>
          <h1> Mission to Mars Registration Form </h1>
          <div>
            <form onChange={this.handleChange}>

              <label htmlFor='Mision'> Mission </label> // MISSION
                <br />
              <input type="text" placeholder="Mission" name="mission" value="" />
              <br />

              <label htmlFor='Name'> What is your name? </label> // NAME
                <br />
              <input type='text' placeholder='Your name' name="name" value="" />
              <br />

              <label htmlFor='Birth'> What is your date of birth? </label>// BIRTH
                <br />
              <input type="date" id="date" name="birth" value="" min="1980-01-01" max="2019-01-01" required/>
              <span class="validity"></span>
              // <input type='text' placeholder='Your birth' name='birth' value='' />
              <br />
              <label htmlFor='country'>What is your country of origin?</label> // COUNTRY
              <select> </select>
            </form>

          </div>
        </>
      )
    }
}

export default Form;
